import { useState, useEffect, useRef } from "react";
import * as XLSX from "xlsx";
import { differenceInDays, parse } from "date-fns";

export default function MaintenanceEditor() {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");
  const [modifiedData, setModifiedData] = useState([]);
  const [selectedRowIndex, setSelectedRowIndex] = useState(null);
  const [filterMode, setFilterMode] = useState("all");
  const [duplicatePlates, setDuplicatePlates] = useState({});
  const [loading, setLoading] = useState(true);
  const [sortConfig, setSortConfig] = useState({ key: null, direction: "asc" });
  const [editCell, setEditCell] = useState({ row: null, key: null });
  const [copiedCell, setCopiedCell] = useState({ row: null, col: null });
  const tableRef = useRef(null);

  // ŸÇÿßÿ¶ŸÖÿ© invygo ŸÉÿßŸÖŸÑÿ©
  const invygoList = [
    "f62443", "e62059", "e48708", "g16281", "f67224", "f60973", "y87421", "g20385", "f10750", "e59684",
    "t64026", "r69915", "t28875", "t88742", "r72392", "t61471", "t61074", "s32761", "s30916", "s34737", "s37248",
    "s32248", "s34138", "s33098", "s33573", "t63147", "s35076", "t93536", "t93285", "t82563", "t58645", "t61724",
    "t61377", "t61450", "t66489", "s37005", "s37285", "r85860", "r73445", "r76490", "r80128", "r80155", "r86003",
    "r75420", "r75429", "r84563", "r88409", "r76317", "r86080", "r85882", "r85160", "aa15916", "aa15002", "aa15920",
    "aa15021", "aa14992", "aa14995", "aa15006", "aa15007", "aa15923", "aa14997", "aa15009", "aa15003", "j66201",
    "aa15004", "aa15008", "r37029", "r36675", "q96589", "r53826", "r51619", "aa15918", "r52488", "r17061", "s72129",
    "r44830", "r22603", "r28968", "j68247", "j94519", "j81598", "j96121", "r36929", "j42625", "r13492", "r36174",
    "s71922", "s75328", "r22937", "s64835", "s64833", "j60185", "j79165", "s61759", "s74924", "s74354", "s70425",
    "s62860", "s70848", "j58197", "j76574", "z48928", "z47957", "aa30014", "z50329", "z49625", "z50325", "z49507",
    "r34461", "t55210", "r17048", "r16713", "t58751", "t56712", "t53810", "r15013", "z48285", "r18683", "z48102",
    "r26125", "t70588", "z47981", "r20497", "r35976", "r36097", "r29460", "r20253", "r35345", "t58875", "r30376",
    "z49428", "r29024", "aa80047", "t54238", "aa80031", "aa29985", "aa80056", "aa80049", "aa29987", "aa80057",
    "aa29982", "aa80051", "aa29983", "aa80052", "aa29984", "aa80053", "aa29986", "aa80054", "aa29980", "cc12935",
    "cc12238", "cc12936", "cc12934", "cc12231", "cc12939", "cc12237", "cc12236", "cc12235", "cc12209", "cc12937",
    "cc12207", "cc12206", "cc12942", "cc12205", "cc12204", "cc12938", "cc12203", "cc12940", "cc12208", "f18326",
    "f18323", "f17754", "f17732", "f17975", "bb43302", "f18583", "bb43307", "f18210", "bb43306", "bb43305",
    "bb43304", "cc13053", "cc10450", "cc10449", "f19054", "f18626", "f19131", "b18431", "f19176", "f17724",
    "f18257", "f18136", "f17514", "f18086", "f18433", "f18320", "cc13071", "f17315", "cc32520", "cc32492",
    "cc32526", "cc31030", "cc32547", "cc32511", "cc32524", "cc31043", "cc32540", "cc32491", "cc31042", "cc31046",
    "cc32558", "cc32554", "cc32501", "cc32553", "cc31041", "cc32538", "cc31038", "cc32493", "cc32510", "cc32557",
    "cc32563", "cc31040", "cc32499", "cc32507", "cc31044", "cc32537", "cc32494", "cc32488", "cc32550", "cc31047",
    "cc32514", "cc32515", "cc32546", "cc32562", "cc32495", "cc32509", "cc32556", "cc32517", "cc32504", "cc32521",
    "cc32549", "cc32496", "cc32560", "cc32561", "cc32527", "cc31036", "cc32535", "cc32544", "q91064", "r34842",
    "r38697", "r36953", "r56260", "r63441", "r54090", "r25270", "r67604", "q96758", "r68227", "r56625",
    "r72343", "q90946", "r70974", "q91745", "r63418", "r35417", "r58495", "r67572", "r67740", "r56549",
    "r63684", "r67519", "r67408", "r43921", "q95245", "r34781", "r71806", "r69331", "r68107", "r65528",
    "r67795", "q96673", "r68609", "r72030", "r69558", "r43518", "r68239", "q93985", "r42977", "r69392",
    "q96453", "r70466", "r68219", "r67749", "q95389", "r72154", "r56530", "r41033"
  ];

  useEffect(() => {
    const fetchSheet = async () => {
      setLoading(true);
      const response = await fetch("https://docs.google.com/spreadsheets/d/1v4rQWn6dYPVQPd-PkhvrDNgKVnexilrR2XIUVa5RKEM/export?format=csv&gid=0");
      const text = await response.text();
      const rows = text
        .split("\n")
        .map(r => r.split(","))
        .filter(r => r.some(cell => cell.trim() !== "")); // Ignore empty rows
      const headers = rows[0];
      const parsed = rows.slice(1).map(r => Object.fromEntries(r.map((v, i) => [headers[i], v])));
      setData(parsed);
      setModifiedData(parsed);
      setLoading(false);
    };
    fetchSheet();
  }, []);

  useEffect(() => {
    const counts = {};
    modifiedData.forEach(row => {
      const plate = row["Vehicle"]?.trim().toLowerCase().replace(/\s+/g, "");
      if (plate) counts[plate] = (counts[plate] || 0) + 1;
    });
    setDuplicatePlates(Object.fromEntries(Object.entries(counts).filter(([_, c]) => c > 1)));
  }, [modifiedData]);

  const today = new Date();

  const parseDate = (str) => {
    if (!str || typeof str !== "string") return new Date("Invalid");
    const formats = ["dd/MM/yyyy", "d-M-yyyy", "dd-MM-yyyy", "yyyy-MM-dd"];
    for (const format of formats) {
      const parsed = parse(str, format, new Date());
      if (!isNaN(parsed)) return parsed;
    }
    const fallback = new Date(str);
    return isNaN(fallback) ? new Date("Invalid") : fallback;
  };

  const getDelayedCars = (rows) => {
    return rows.filter(row => {
      const damageKey = Object.keys(row).find(k => k.toLowerCase().includes("damag"));
      const dateOutKey = Object.keys(row).find(k => k.toLowerCase().includes("date out"));
      const dateInKey = Object.keys(row).find(k => k.toLowerCase().includes("date in"));

      const damage = damageKey && row[damageKey]?.toLowerCase();
      const dateOutStr = dateOutKey && row[dateOutKey]?.trim();
      const dateIn = dateInKey && row[dateInKey]?.trim();

      if (!dateOutStr || dateIn) return false;

      const dateOut = parseDate(dateOutStr);
      if (isNaN(dateOut)) return false;

      const daysPassed = differenceInDays(today, dateOut);

      if (damage?.includes("oil") && daysPassed > 3) return true;
      if (damage?.includes("accident") && daysPassed > 30) return true;
      if (!damage?.includes("oil") && !damage?.includes("accident") && daysPassed > 3) return true;

      return false;
    });
  };

  const delayedCars = getDelayedCars(modifiedData);

  // ÿØŸÖÿ¨ ÿ¥ÿ±Ÿàÿ∑ ÿßŸÑŸÅŸÑÿ™ÿ±ÿ© ŸÖÿπ ÿßŸÑÿ®ÿ≠ÿ´
  const filtered = modifiedData.filter(row => {
    const matchesSearch = search.trim() === "" || Object.values(row).some(v => v.toLowerCase().includes(search.toLowerCase()));
    const dateInKey = Object.keys(row).find(k => k.toLowerCase().includes("date in"));
    const damageKey = Object.keys(row).find(k => k.toLowerCase().includes("damag"));
    const damageText = damageKey && row[damageKey]?.toLowerCase();
    const hasDate = dateInKey && row[dateInKey]?.trim() !== "";
    const plate = row["Vehicle"]?.trim().toLowerCase().replace(/\s+/g, "");

    if (filterMode === "accident") return matchesSearch && damageText?.includes("accident") && !hasDate;
    if (filterMode === "invygo") return matchesSearch && plate && invygoList.includes(plate) && !hasDate;
    if (filterMode === "notready") return matchesSearch && !hasDate;
    if (filterMode === "duplicates") {
      if (!plate || !duplicatePlates[plate]) return false;
      const allSamePlate = modifiedData.filter(
        r => (r["Vehicle"]?.trim().toLowerCase().replace(/\s+/g, "") || "") === plate
      );
      const anyHasDateIn = allSamePlate.some(r => {
        const dateInKey = Object.keys(r).find(k => k.toLowerCase().includes("date in"));
        return dateInKey && r[dateInKey]?.trim() !== "";
      });
      if (anyHasDateIn) return false;
      return matchesSearch;
    }
    if (filterMode === "delayed") return matchesSearch && delayedCars.includes(row);
    // Show All OR search
    return matchesSearch;
  });

  // Sorting logic
  const sortedData = (() => {
    if (!sortConfig.key) return filtered;
    return [...filtered].sort((a, b) => {
      const aVal = a[sortConfig.key] || "";
      const bVal = b[sortConfig.key] || "";
      if (aVal < bVal) return sortConfig.direction === "asc" ? -1 : 1;
      if (aVal > bVal) return sortConfig.direction === "asc" ? 1 : -1;
      return 0;
    });
  })();

  const handleSort = (key) => {
    // If click on # column, reset sort
    if (key === "__index__") {
      setSortConfig({ key: null, direction: "asc" });
      return;
    }
    setSortConfig((prev) => {
      if (prev.key === key) {
        return { key, direction: prev.direction === "asc" ? "desc" : "asc" };
      }
      return { key, direction: "asc" };
    });
  };

  // Inline editing
  const handleCellDoubleClick = (rowIdx, key) => {
    setEditCell({ row: rowIdx, key });
  };

  const handleCellChange = (e, rowIdx, key) => {
    const value = e.target.value;
    setModifiedData((prev) => {
      const updated = [...prev];
      updated[rowIdx] = { ...updated[rowIdx], [key]: value };
      return updated;
    });
  };

  const handleCellBlur = () => setEditCell({ row: null, key: null });

  const handleCellKeyDown = (e) => {
    if (e.key === "Enter" || e.key === "Escape") handleCellBlur();
  };

  // Copy cell content on click (except index column)
  const handleCellClick = (rowIdx, colKey, value) => {
    if (colKey === "__index__") return;
    navigator.clipboard.writeText(value);
    setCopiedCell({ row: rowIdx, col: colKey });
    setTimeout(() => setCopiedCell({ row: null, col: null }), 900);
  };

  const getRowStyle = (row) => {
    const dateInKey = Object.keys(row).find(k => k.toLowerCase().includes("date in"));
    const damageKey = Object.keys(row).find(k => k.toLowerCase().includes("damag"));
    const dateIn = dateInKey && row[dateInKey]?.trim();
    const damageText = damageKey && row[damageKey]?.toLowerCase();
    const plate = row["Vehicle"]?.trim().toLowerCase().replace(/\s+/g, "");

    const isInvygo = invygoList.includes(plate);
    const isAccident = damageText?.includes("accident") && !dateIn;
    const isReady = Boolean(dateIn);

    if (plate && duplicatePlates[plate]) return { backgroundColor: "#e53935", color: "#fff" };
    if (delayedCars.includes(row)) return { backgroundColor: "#ff7043", color: "#fff" };
    if (isInvygo && isReady) return { backgroundColor: "#81c784" };
    if (isInvygo && !isReady) return { backgroundColor: "#64b5f6" };
    if (!isInvygo && isReady) return { backgroundColor: "#ce93d8" };
    if (!isInvygo && !isReady) return { backgroundColor: "#ffd600" };
    if (isAccident) return { backgroundColor: "#ef9a9a" };
    return {};
  };

  const exportToExcel = () => {
    const ws = XLSX.utils.json_to_sheet(sortedData);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Maintenance");
    XLSX.writeFile(wb, `Maintenance_${new Date().toISOString().slice(0, 10)}.xlsx`);
  };

  return (
    <div style={{ padding: 20, fontFamily: "Segoe UI", position: "relative", background: "#fffde7" }}>
      {/* Legend at the top */}
      <div style={{ display: "flex", justifyContent: "center", gap: 20, flexWrap: "wrap", marginBottom: 24 }}>
        <div style={{ background: "#81c784", padding: "4px 8px", borderRadius: 6, color: "#000" }}>‚¨§ Invygo - Repaired</div>
        <div style={{ background: "#64b5f6", padding: "4px 8px", borderRadius: 6, color: "#000" }}>‚¨§ Invygo - Not Ready</div>
        <div style={{ background: "#ce93d8", padding: "4px 8px", borderRadius: 6, color: "#000" }}>‚¨§ Yelo - Repaired</div>
        <div style={{ background: "#ef9a9a", padding: "4px 8px", borderRadius: 6, color: "#000" }}>‚¨§ Accident - Not Ready</div>
        <div style={{ background: "#ffd600", padding: "4px 8px", borderRadius: 6, color: "#000" }}>‚¨§ Yelo - Not Ready</div>
        <div style={{ background: "#ff7043", padding: "4px 8px", borderRadius: 6, color: "#fff" }}>‚¨§ ‚è± Delayed</div>
      </div>

      <a
        href="https://moalamir52.github.io/Yelo/#dashboard"
        style={{
          position: "absolute",
          top: 80,
          left: 200,
          backgroundColor: "#ffd600",
          color: "#6a1b9a",
          padding: "10px 20px",
          textDecoration: "none",
          fontWeight: "bold",
          borderRadius: "8px",
          border: "2px solid #6a1b9a",
          boxShadow: "0 4px 16px 0 #6a1b9a55"
        }}
      >
        üîô Back to YELO
      </a>

      <h2
        style={{
          textAlign: "center",
          color: "#6a1b9a",
          background: "#ffd600",
          borderRadius: 12,
          fontWeight: "bold",
          fontSize: 32,
          letterSpacing: 1,
          margin: "20px auto 18px auto",
          padding: "16px 0",
          boxShadow: "0 8px 32px 0 #6a1b9a99",
          maxWidth: 520,
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          gap: 14
        }}
      >
        <span style={{ fontSize: 36 }}>üöó</span>
        Maintenance Sheet Editor
      </h2>

      {/* Filter Buttons */}
      <div style={{ display: "flex", justifyContent: "center", flexWrap: "wrap", gap: 10, marginBottom: 10 }}>
        {[
          { key: "accident", label: "üöó Car Accident" },
          { key: "invygo", label: "üì¶ Invygo Cars" },
          { key: "notready", label: "‚ùå Not Ready" },
          { key: "duplicates", label: "üõë Duplicates" },
          { key: "delayed", label: "‚è± Show Delayed" },
          { key: "all", label: "üîÑ Show All" },
        ].map(btn => (
          <button
            key={btn.key}
            onClick={() => setFilterMode(btn.key)}
            style={{
              backgroundColor: filterMode === btn.key ? "#6a1b9a" : "#ffd600",
              border: filterMode === btn.key ? "2px solid #ffd600" : "1px solid #6a1b9a",
              color: filterMode === btn.key ? "#fff" : "#4a148c",
              padding: "8px 14px",
              borderRadius: "8px",
              fontWeight: "bold",
              cursor: "pointer",
              fontSize: "13px",
              transition: "0.2s",
              boxShadow: "0 8px 32px 0 #6a1b9a99"
            }}
          >
            {btn.label}
          </button>
        ))}
      </div>

      {/* Search Box - directly above the table */}
      <div style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        marginBottom: 18,
      }}>
        <div style={{
          display: "flex",
          alignItems: "center",
          background: "#fff",
          borderRadius: "16px",
          boxShadow: "0 4px 16px #6a1b9a55",
          padding: "8px 18px",
          minWidth: 320,
          border: "2px solid #ffd600"
        }}>
          <span style={{ fontSize: 22, color: "#6a1b9a", marginRight: 8 }}>üîç</span>
          <input
            type="text"
            placeholder="Search..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            style={{
              border: "none",
              outline: "none",
              background: "transparent",
              fontSize: "1.1em",
              flex: 1,
              padding: "8px",
              fontFamily: "inherit",
              color: "#6a1b9a"
            }}
          />
        </div>
      </div>

      {/* Export Button */}
      <div style={{ display: "flex", justifyContent: "center", marginBottom: 20 }}>
        <button
          onClick={exportToExcel}
          style={{
            background: "#6a1b9a",
            color: "#fff",
            padding: "8px 18px",
            borderRadius: "8px",
            border: "none",
            fontWeight: "bold",
            fontSize: "15px",
            cursor: "pointer",
            boxShadow: "0 4px 16px 0 #6a1b9a99"
          }}
        >
          üì• Export to Excel
        </button>
      </div>

      {loading ? (
        <div style={{ textAlign: "center", margin: "40px 0", fontSize: 22, color: "#6a1b9a" }}>
          ‚è≥ Loading data...
        </div>
      ) : (
        <>
          <div style={{ textAlign: "center", marginBottom: 10, color: "#555" }}>‚úÖ Showing {sortedData.length} result(s)</div>
          {delayedCars.length > 0 && (
            <div style={{ textAlign: "center", marginBottom: 10, color: "red", fontWeight: "bold" }}>
              üö® There are {delayedCars.length} cars delayed beyond the allowed time in the workshop!
            </div>
          )}

          <div ref={tableRef} style={{
            overflowX: "auto",
            maxHeight: "70vh",
            borderRadius: 12,
            boxShadow: "0 4px 32px 0 #6a1b9a66",
            background: "#fff",
            marginBottom: 30
          }}>
            <table style={{
              borderCollapse: "collapse",
              width: "100%",
              border: "2px solid #000",
              borderRadius: 12,
              overflow: "hidden"
            }}>
              <thead>
                <tr style={{
                  position: "sticky",
                  top: 0,
                  zIndex: 2,
                  background: "#ffd600",
                  color: "#6a1b9a"
                }}>
                  <th
                    style={{ border: "1px solid #000", padding: 8, cursor: "pointer", userSelect: "none", background: "#ffd600" }}
                    onClick={() => handleSort("__index__")}
                  >#</th>
                  {Object.keys(modifiedData[0] || {}).map((key) => (
                    <th
                      key={key}
                      style={{ border: "1px solid #000", padding: 8, cursor: "pointer", userSelect: "none", background: "#ffd600" }}
                      onClick={() => handleSort(key)}
                    >
                      {key}
                      {sortConfig.key === key ? (sortConfig.direction === "asc" ? " ‚ñ≤" : " ‚ñº") : ""}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {sortedData.map((row, rowIndex) => (
                  <tr key={rowIndex} style={{
                    ...getRowStyle(row),
                    backgroundColor: selectedRowIndex === rowIndex ? "#c5cae9" : getRowStyle(row).backgroundColor
                  }}>
                    <td
                      style={{ border: "1px solid #000", padding: 6, textAlign: "center", cursor: "pointer", background: "#fffde7" }}
                      onClick={() => setSortConfig({ key: null, direction: "asc" })}
                    >
                      {rowIndex + 1}
                    </td>
                    {Object.entries(row).map(([key, value], colIndex) => (
                      <td
                        key={colIndex}
                        style={{ border: "1px solid #000", padding: 6, cursor: "pointer", position: "relative" }}
                        onClick={() => handleCellClick(rowIndex, key, value)}
                        onDoubleClick={() => handleCellDoubleClick(rowIndex, key)}
                      >
                        {editCell.row === rowIndex && editCell.key === key ? (
                          <input
                            value={value}
                            autoFocus
                            onChange={e => handleCellChange(e, rowIndex, key)}
                            onBlur={handleCellBlur}
                            onKeyDown={handleCellKeyDown}
                            style={{
                              width: "100%",
                              border: "1.5px solid #ffd600",
                              background: "#fffde7",
                              color: "#000",
                              borderRadius: 4,
                              padding: "2px 4px"
                            }}
                          />
                        ) : (
                          <>
                            <input
                              value={value}
                              readOnly
                              style={{
                                width: "100%",
                                border: "none",
                                background: "transparent",
                                pointerEvents: "none",
                                color: "#000"
                              }}
                            />
                            {copiedCell.row === rowIndex && copiedCell.col === key && (
                              <span style={{
                                position: "absolute",
                                top: 2,
                                right: 8,
                                background: "#ffd600",
                                color: "#6a1b9a",
                                fontWeight: "bold",
                                fontSize: 12,
                                borderRadius: 4,
                                padding: "1px 6px",
                                boxShadow: "0 2px 8px #ffd60055",
                                zIndex: 2
                              }}>Copied!</span>
                            )}
                          </>
                        )}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      )}
    </div>
  );
}
